pir_motion.code:
#define PIR_PIN 2
#define LED_PIN 13

void setup() {
  pinMode(PIR_PIN, INPUT);
  pinMode(LED_PIN, OUTPUT);
  Serial.begin(9600);
  Serial.println("Warming up PIR (60s)...");
  delay(60000);  // PIR needs 60s warmup
  Serial.println("Ready!");
}
void loop() {
  if (digitalRead(PIR_PIN) == HIGH) {
    digitalWrite(LED_PIN, HIGH);
    Serial.println("Motion detected!");
    delay(2000);  // Debounce
  } else {
    digitalWrite(LED_PIN, LOW);
  }
}

l293d_motor.code:
// L293D pins
#define EN1 5   // PWM speed control
#define IN1 6   // Direction
#define IN2 7   // Direction

void setup() {
  pinMode(EN1, OUTPUT);
  pinMode(IN1, OUTPUT);
  pinMode(IN2, OUTPUT);
}

void forward(int speed) {
  digitalWrite(IN1, HIGH);
  digitalWrite(IN2, LOW);
  analogWrite(EN1, speed);  // 0-255
}

void backward(int speed) {
  digitalWrite(IN1, LOW);
  digitalWrite(IN2, HIGH);
  analogWrite(EN1, speed);
}

void stop() {
  digitalWrite(IN1, LOW);
  digitalWrite(IN2, LOW);
  analogWrite(EN1, 0);
}

void loop() {
  forward(200);
  delay(2000);
  stop();
  delay(500);
  backward(200);
  delay(2000);
  stop();
  delay(500);
}

74hc595_shift.code:
#define DATA  11  // DS
#define CLOCK 12  // SHCP
#define LATCH 8   // STCP

void setup() {
  pinMode(DATA, OUTPUT);
  pinMode(CLOCK, OUTPUT);
  pinMode(LATCH, OUTPUT);
}

void shiftWrite(byte data) {
  digitalWrite(LATCH, LOW);
  shiftOut(DATA, CLOCK, MSBFIRST, data);
  digitalWrite(LATCH, HIGH);
}

void loop() {
  // Light LEDs one by one
  for (int i = 0; i < 8; i++) {
    shiftWrite(1 << i);
    delay(100);
  }
  // All on
  shiftWrite(0xFF);
  delay(500);
  // All off
  shiftWrite(0x00);
  delay(500);
}

